mapper.update:
update sys_users
username=#{username}, password=#{password}, salt=#{salt}, email=#{email}, mobile=#{mobile}, deptId=#{deptId}, modifiedUser=#{modifiedUser},modifiedTime = now()
where id = #{id}

mapper.select:
SELECT u.id, u.username, u.password, u.salt, u.email, u.mobile, u.valid, u.deptId, u.createdTime, u.modifiedTime, u.createdUser, u.modifiedUser, d.name deptName from sys_users u left join sys_depts d on u.deptId = d.id
username like concat("%",#{key},"%")
ORDER BY createdTime LIMIT #{startIndex},#{pageSize}
